import React, { useState } from "react";
import "./App.css";

const aircraftLayout = [
  [
    { label: "4A", row: 0, col: 0 },
    { label: "4B", row: 0, col: 1 },
    { label: "4C", row: 0, col: 2 },
    { label: "4D", row: 0, col: 3 },
    { label: "4E", row: 0, col: 4 },
    { label: "4F", row: 0, col: 5 },
  ],
  [
    { label: "5A", row: 1, col: 0 },
    { label: "5B", row: 1, col: 1 },
    { label: "5C", row: 1, col: 2 },
    { label: "5D", row: 1, col: 3 },
    { label: "5E", row: 1, col: 4 },
    { label: "5F", row: 1, col: 5 },
  ],
  [
    { label: "6A", row: 2, col: 0 },
    { label: "6B", row: 2, col: 1 },
    { label: "6C", row: 2, col: 2 },
    { label: "6D", row: 2, col: 3 },
    { label: "6E", row: 2, col: 4 },
    { label: "6F", row: 2, col: 5 },
  ],
  [
    { label: "7A", row: 3, col: 0 },
    { label: "7B", row: 3, col: 1 },
    { label: "7C", row: 3, col: 2 },
    { label: "7D", row: 3, col: 3 },
    { label: "7E", row: 3, col: 4 },
    { label: "7F", row: 3, col: 5 },
  ],
];

const Seat = ({ label, row, col }) => (
  <div className={`seat ${label ? "occupied" : ""}`}>{label}</div>
);

const App = () => {
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [buttonColor, setButtonColor] = useState("blue");

  const handleSeatClick = (seat) => {
    setSelectedSeats((prevSelectedSeats) => {
      if (prevSelectedSeats.includes(seat)) {
        return prevSelectedSeats.filter((s) => s !== seat);
      } else {
        return [...prevSelectedSeats, seat];
      }
    });

    setButtonColors((prevColor) => ({
      ...prevColor,
      [seat.label]: prevColor[seat.label] === "blue" ? "orange" : "blue",
    }));
  };

  return (
    <div className="plane">
      {aircraftLayout.map((row, rowIndex) => (
        <div className="row" key={rowIndex}>
          {row.map((seat, seatIndex) => (
            <button
              key={seat.label}
              onClick={() => handleSeatClick(seat)}
              style={{
                backgroundColor: buttonColor[seat.label] || "blue",
              }}
            >
              {console.log(selectedSeats)}
              <Seat label={seat.label} row={seat.row} col={seat.col} />
            </button>
          ))}
        </div>
      ))}
    </div>
  );
};

export default App;

