  ["20A", "20B", "20C", "20D", "20E", "20F"],
  ["21A", "21B", "21C", "21D", "21E", "21F"],
  ["22A", "22B", "22C", "22D", "22E", "22F"],
];

const Seat = ({ label }) => (
  <div className={`seat ${label ? "occupied" : ""}`}>{label}</div>
);

const App = () => {
  return (
    <div className="plane">
      {seats.map((row, rowIndex) => (
        <div className="row" key={rowIndex}>
          {row.map((seat, seatIndex) => (
            <Seat key={seatIndex} label={seat} />
          ))}
        </div>
      ))}
    </div>
  );
};

export default App;


